import Brand from "../../components/Brand";
import {useForm} from "react-hook-form";
import {getMouvementSolde, rechargeCompte} from "../../services/SoldeController";
import React, {useEffect, useState} from "react";

export default function Rechargement(){

    const{handleSubmit, register} = useForm()
    const[message, setMessage] = useState(null);
    const[mouvement, setMouvement] = useState(null)

    useEffect(()=>{
        console.log("meric")
        getMouvementSolde().then(res=>{
            console.log(res.data)
            setMouvement(res.data.mouvements)
        })
    }, [])

    function submit(data){
        data.client = JSON.parse(sessionStorage.getItem("client"));
        // console.log(data)
        rechargeCompte(data).then(res => {
            setMessage(res.data.message)

        })
    }

    if (!mouvement){
        return (
            <div className="preloader">
                <div className="loader">
                    <span></span>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
            </div>
        )
    }else{
        return(
            <>
                <Brand></Brand>
                <div className="row mt-5">
                    <div className="col-md-4">
                        <div className="form-wrapper">
                            <div className="form-title">
                                <h3>Recharger compte</h3>
                            </div>
                            {message ?
                                <div className="alert alert-info" role="alert">
                                    {message}
                                </div> : ""
                            }

                            <form className="w-100" onSubmit={handleSubmit(submit)}>
                                <div className="row">
                                    <div className="col-12">
                                        <div className="form-inner">
                                            <label>Montant</label>
                                            <input {...register("montant")} type="number" step="any" placeholder="Entrer le nom du produit" />
                                        </div>
                                    </div>
                                    <div className="col-12">
                                        <div className="form-inner">
                                            <label>Mouvement</label>
                                            <select {...register("idMovement")} id="">
                                                {mouvement.map(mvm =>
                                                    <option value="">{mvm.nom}</option>
                                                )}
                                            </select>
                                        </div>
                                    </div>
                                </div>
                                <button className="account-btn">Enregistrer</button>
                            </form>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}
