import React, {Component} from "react";
import Brand from "../components/Brand";
import {getAuction} from "../services/EnchereService";
export default class Enchere extends Component{
    constructor(props) {
        super(props);
        this.state = {
            auctions: [],
            load: false
        }
    }

    componentDidMount() {
        getAuction().then(res => {
            this.setState({
                auctions: res.data,
                load: true
            })
        })

        /*this.setState({
            auctions: [{
                id: 1,
                date: '2021-6-10',
                valid: false
            }],
            load: true
        })*/

        // console.log(this.state)
    }


    detail(id){

        const auction = this.state.auctions.find(auction => auction.id === id)
        sessionStorage.setItem("auction", JSON.stringify(auction));
        window.location.href = "/detail-enchere";
    }



    render() {
        console.log(this.state.auctions)
        return(
            <div>
                <Brand></Brand>
                <div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-9">
                        <div className="live-auction-section pt-120 pb-120">
                            <img alt="image" src={require("../assets/images/bg/section-bg.png")} className="img-fluid section-bg-top" />
                            <img alt="image" src={require("../assets/images/bg/section-bg.png")} className="img-fluid section-bg-bottom" />
                            <div className="container">
                                {this.state.auctions.map(auction =>
                                    <div className="row gy-4 mb-60 d-flex justify-content-center">
                                        <div className="col-lg-4 col-md-6 col-sm-10 ">
                                            <div data-wow-duration="1.5s" data-wow-delay="0.2s"
                                                 className="eg-card auction-card1 wow fadeInDown">
                                                <div className="auction-img">
                                                </div>
                                                <div className="auction-content">
                                                    <h4><a >Brand New royal Enfield 250 CC
                                                        For Sale</a></h4>
                                                    <p>Bidding Price : <span>{auction.prixMin}</span></p>
                                                    <div className="auction-card-bttm">
                                                        <a onClick={() => this.detail(auction.id)} className="eg-btn btn--primary btn--sm">Place a Bid</a>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>)}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
