import React, {Component, useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import {getAuctionDetail} from "../../services/EnchereService";
import Brand from "../Brand";
import Footer from "../Footer";
import OffreEnchere from "./OffreEnchere";

export default function DetailEnchere(props){
    const params = useParams();
    const [load, isLoad] = useState(false);
    const [data, setData] = useState(null);
    const [component, setComponent] = useState(null);

    useEffect(()=>{
        /*getAuctionDetail(params.id).then(res => {
            setDate(res.data);
            isLoad(true)
            console.log(data)
        })*/
        console.log(props.data)
        setData(props.data)
        isLoad(true)
    }, [])

     function pageDescription(){
        return(
            <div className="tab-content" id="pills-tabContent">
                <div className="tab-pane fade show active wow fadeInUp" data-wow-duration="1.5s"
                     data-wow-delay=".2s" id="pills-home" role="tabpanel"
                     aria-labelledby="pills-home-tab">
                    <div className="describe-content">
                        {/*<h4>How can have anything you ant in life if you ?</h4>*/}
                        <p className="para">
                            {data.description}
                        </p>
                    </div>
                </div>
            </div>
        )
    }

    function description(){
        setComponent(pageDescription)
    }

    function offre(e){
        e.target.classList.toggle("active")
        setComponent(()=> {
             return (
                <OffreEnchere></OffreEnchere>
            )
        })
    }

    if (!data){
        return (
            <div className="preloader">
                <div className="loader">
                    <span></span>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
            </div>
        )
    }
    return(
        <>
            <Brand></Brand>
            <div className="auction-details-section pt-120">
                <img alt="image" src={require("../../assets/images/bg/section-bg.png")} className="img-fluid section-bg-top" />
                <img alt="image" src={require("../../assets/images/bg/section-bg.png")} className="img-fluid section-bg-bottom" />
                <div className="container">
                    <div className="row g-4 mb-50">
                        <div className="col-xl-6 col-lg-7">
                            <ul className="nav nav-pills d-flex flex-row justify-content-start gap-sm-4 gap-3 mb-45 wow fadeInDown"
                                data-wow-duration="1.5s" data-wow-delay=".2s" id="pills-tab" role="tablist">
                                <li className="nav-item" role="presentation">
                                    <button className="nav-link details-tab-btn " onClick={offre}  type="button">
                                        History
                                    </button>
                                </li>
                            </ul>
                            {component}
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
