import React, {Component, useCallback} from "react";
import {getAllCategories} from "../services/CategoriesService";
import debounce from 'lodash.debounce';

export default class Recherche extends Component{
    categories
    constructor(props) {
        super(props);
        this.state = {}
        /*this.state = {
            produit: undefined,
            description: undefined,
            prixMinMax: undefined,
            prixMinMin: undefined,
            dateDebutMax: undefined,
            dateDebutMin: undefined,
            durreeMin: undefined,
            durreMax: undefined,
            idCategorie: undefined,
        }*/

        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleToggle = debounce(() => {
        this.setState(prevState => ({ show: !prevState.show }));
    }, 500);

    componentDidMount() {
        getAllCategories().then(res=>{
            this.categories = res.data
        })
    }

    async handleInputChange(event) {
        console.log(event)
        event.preventDefault();
        const target = event.target;
        this.setState({
            [target.name]: target.value,
        });

        console.log(await this.state)
    }

    debouncedChangeHandler(e) {
        console.log(e)
        debounce(() =>{
            console.log(e)
            e.preventDefault();
            const target = e.target;
            this.setState({
                [e.target.name]: e.target.value,
            });
            console.log(this.state)
        }, 300)
    }
    render() {

        return(
            <div className="mt-5">
                <div className="form-wrapper" >
                    <div className="form-title">
                        <h3>Recherche</h3>
                    </div>
                    {/*<button onClick={this.handleToggle}>Toggle</button>*/}
                    <form className="w-100">
                        <div className="row">
                            <div className="col-12">
                                <div className="form-inner">
                                    <label>Produit</label>
                                    <input 
                                           onChange={this.debouncedChangeHandler} name="produit" type="text" placeholder="Entrer le nom du produit" />
                                </div>
                            </div>
                            <div className="col-6">
                                <div className="form-inner">
                                    <label>Prix Min</label>
                                    <input type="number" name="prixMinMin" onInput={this.handleInputChange} id="password" placeholder="prix min" />
                                </div>
                            </div>
                            <div className="col-6">
                                <div className="form-inner">
                                    <label>Prix Max</label>
                                    <input type="number" name="prixMinMax" id="password" placeholder="prix max" />
                                </div>
                            </div>
                            <div className="col-6">
                                <div className="form-inner">
                                    <label>Date debut Min</label>
                                    <input type="date" name="dateDebutMin" onInput={this.handleInputChange} id="password" placeholder="date debut min" />
                                </div>
                            </div>
                            <div className="col-6">
                                <div className="form-inner">
                                    <label>Date debut max</label>
                                    <input type="date" name="dateDebutMax" id="dateDebutMax" placeholder="date debut max" />
                                </div>
                            </div>
                            <div className="col-6">
                                <div className="form-inner">
                                    <label>Duree min</label>
                                    <input type="time" name="dureeMin" id="dureeMin" placeholder="duree max" />
                                </div>
                            </div>
                            <div className="col-6">
                                <div className="form-inner">
                                    <label>Duree Max</label>
                                    <input type="time" name="dureeMax" id="dureeMax" placeholder="duree max" />
                                </div>
                            </div>
                        </div>
                        <button className="btn btn-outline-primary">Log in</button>
                    </form>
                </div>
            </div>
        )
    }


}
